import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser } from '@fortawesome/free-regular-svg-icons';
import React from 'react'
import { useState } from 'react';
import { Link } from 'react-router-dom'

import './LogIn.css'
export default function LogIn() {



    //-------Show New Password------//

    const [passwordType, setPasswordType] = useState("password");
    const [passwordInput, setPasswordInput] = useState("");
    const handlePasswordChange = (evnt) => {
        setPasswordInput(evnt.target.value);
    }




    const togglePassword = (e) => {
        e.preventDefault()


        if (passwordType === "password") {
            setPasswordType("text")
            return;
        }
        setPasswordType("password")
    }



    //----------Controle Reffreshing page---------// 

    const signUp = (e) => {
        e.preventDefault()
        try {
        } catch (error) {
            console.log(error.message)
        }
    }



    return (
        <div className='container'>
            <div className="row">
                <div className="col md-6 sm-12">
                    <div className="LogInCard  ">
                        <div className="card " style={{ width: '20rem', height: "35rem", }}>
                            <div class="card-body">
                                <h3 class="card-title text-center fw-bold mt-4">Login</h3>
                                <form>
                                    <div class="mb-2 mt-3">
                                        <label for="exampleInputEmail1" className="form-label fw-bold">
                                            Username</label>
                                        <input type="text" className="form-control userName" placeholder='Type your username' />
                                    </div>
                                    <div class="mb-3">
                                        <label for="exampleInputPassword1" className="form-label mb-0 fw-bold">Password</label>
                                        <input type={passwordType} onChange={handlePasswordChange} value={passwordInput} name="password" className="form-control password" placeholder="Password" />
                                        <input type="checkbox" name="ShowPassword" id="ShowPassword" className='m-2' onClick={togglePassword} />Show Password
                                    </div>
                                    <div>
                                        <a href="javascript:void(0)" className='forgatPass'>Forget password?</a>
                                    </div>
                                    <div className=''>
                                        <button type="submit" className="btn logInBtn  mt-3 w-100 text-light" onClick={signUp}>Login</button>
                                    </div>
                                </form>
                                <div>
                                    <p className='text-center mt-3 mb-0'>Or Sign Up Using</p>
                                    <div className='SignUpIcons mt-2'>
                                        <a href="javascript:void(0)"><i className="bi-facebook p-1 " role="img" aria-label="facebook"></i></a>
                                        <a href="javascript:void(0)"> <i className="bi-twitter p-1" role="img" aria-label="twitter"></i></a>
                                        <a href="javascript:void(0)"> <i className="bi-google p-1" role="img" aria-label="google"></i></a>
                                    </div>
                                </div>
                                <div className='text-center'>
                                    <p className=' mt-3 mb-2'>Or Sign Up Using</p>
                                    <Link to='/signUp' className='SignUp-Link'>SignUp</Link>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
